import "./App.css";
import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { SeccionTask } from "./components/SeccionTask";
import { NewTask } from "./components/NewTask";
import { Alert } from "react-bootstrap";
import { ModalForm } from "./components/Modal";
function App() {
  const [visible, setVisible] = useState(false)
  return (
    <>
      <div className="container">
        <h1 className="text-center p-2"> Lista de tareas</h1>
        <div className="row justify-content-center">
          <div className="col-12 col-md-5">
            <input placeholder="Buscar tareas" className="form-control" />
          </div>
        </div>
        <div className="row justify-content-center mt-4">
          {[1, 2, 3, 4, 5].map((item) => (
            <div className="col-12 col-md-7 mt-3" key={item}>
              <SeccionTask />
            </div>
          ))}
          <div className="col-12 col-md-7">
            <Alert variant={"danger"}>No hay tareas disponibles!</Alert>
          </div>
        </div>

        <h3 className="p-2">Completados</h3>
        <div className="row justify-content-center mt-4">
          {[1, 2].map((item) => (
            <div className="col-12 col-md-7 mt-3" key={item}>
              <SeccionTask />
            </div>
          ))}
        </div>
        <div className="row justify-content-center mt-4">
          <div className="col-5">
            <button className="btn btn-info form-control">Nueva tarea</button>
          </div>
        </div>
        <ModalForm open={true}/>
        <NewTask />
      </div>
    </>
  );
}

export default App;
