import React from "react";

export const SeccionTask = ({task,setTasks, tasks}) => {
    const checkTask = (check) => {
        const findTask = tasks.find(item => item.id === task.id)
        findTask.complete = check
        const filterTask = tasks.filter(item => item.id !== task.id)
        filterTask.push(findTask)
        setTasks(filterTask)
    }
    const deleteTask = () => {
        const filterTask = tasks.filter(item => item.id !== task.id)
        setTasks(filterTask)
    }

  return (
    <div className="d-flex mb-1 seccionTask align-items-center p-1">
      <input type="checkbox" className="p-2" onChange={event => checkTask(event.target.checked)} defaultChecked={task.complete}/>
      <span className="p-1">{task.task}</span>
      <span className="p-1 ms-auto text-danger" onClick={deleteTask}>Eliminar</span>

    </div>
  );
};
